    private void xuLySuKien() {
        nutQuayLai.setOnClickListener(v -> finish());

        // Bấm nút hoàn thành sẽ tính điểm
        btnHoanThanh.setOnClickListener(v -> chuyenSangTrangKetQua());
    }

    private void goiApiLayCauHoi(int baiTapId) {
        ApiService apiService = ApiClient.getClient(this).create(ApiService.class);
        apiService.getCauHoiByBaiTapId(baiTapId).enqueue(new Callback<List<CauHoiResponse>>() {
            @Override
            public void onResponse(Call<List<CauHoiResponse>> call, Response<List<CauHoiResponse>> response) {
                if (response.isSuccessful() && response.body() != null) {
                    danhSachCauHoi.clear();
                    List<CauHoiResponse> listTuServer = response.body();

                    for (CauHoiResponse ch : listTuServer) {
                        ch.xuLyDuLieu(); // Parse JSON để lấy đáp án A, B, C, D và Correct
                        danhSachCauHoi.add(ch);
                    }

                    adapter.notifyDataSetChanged();
                    thanhTienTrinh.setMax(danhSachCauHoi.size());
                    capNhatTrangThaiTienTrinh();

                } else {
                    Toast.makeText(BaiTapDocActivity.this, "Bài tập này chưa có câu hỏi!", Toast.LENGTH_SHORT).show();
                }
            }

            @Override
            public void onFailure(Call<List<CauHoiResponse>> call, Throwable t) {
                Log.e("API_ERROR", "Lỗi: " + t.getMessage());
                Toast.makeText(BaiTapDocActivity.this, "Lỗi kết nối server", Toast.LENGTH_SHORT).show();
            }
        });
    }

    // --- SỬA ĐỔI 2: LOGIC TÍNH ĐIỂM CHÍNH XÁC ---
    private void chuyenSangTrangKetQua() {
        boDemGio.removeCallbacks(tacVuDemGio);
        int soCauDung = 0;

        // Tạo danh sách câu trả lời để gửi lên API
        List<CauTraLoiRequest> danhSachCauTraLoi = new ArrayList<>();

        // Duyệt qua tất cả câu hỏi trong danh sách
        for (CauHoiResponse cauHoi : danhSachCauHoi) {
            int id = cauHoi.getId();

            // Lấy đáp án đúng của câu hỏi (Từ JSON database đã xử lý)
            String dapAnDungHeThong = cauHoi.getDapAnDung(); // Ví dụ: "Father"
            String dapAnDungRaw = cauHoi.getDapAnDungRaw(); // Ví dụ: "A", "B", "C", "D"

            // Lấy đáp án người dùng chọn từ Map
            String dapAnNguoiDungChon = danhSachDapAnNguoiDung.get(id); // Ví dụ: "Father"

            // So sánh (Dùng equals để so sánh chuỗi)
            if (dapAnNguoiDungChon != null && dapAnDungHeThong != null) {
                if (dapAnNguoiDungChon.trim().equalsIgnoreCase(dapAnDungHeThong.trim())) {
                    soCauDung++;
                }
            }

            // Thêm vào danh sách câu trả lời
            // userAns: Nội dung người dùng chọn (hoặc empty nếu không chọn)
            // correctAns: Nội dung đáp án đúng
            String userAnswer = dapAnNguoiDungChon != null ? dapAnNguoiDungChon : "";
            danhSachCauTraLoi.add(new CauTraLoiRequest(id, userAnswer, dapAnDungHeThong));
        }

        // Tạo object LichSuBaiTapRequest để gửi lên API
        LichSuBaiTapRequest lichSuRequest = new LichSuBaiTapRequest(
                null,                           // idTest: null vì là bài tập
                idBaiTapHienTai,               // idBaiTap
                tenBaiTap,                     // tenBai
                "BAI_TAP",                     // loaiBai: "BAI_TAP" hoặc "TEST"
                soGiayLamBai,                  // tgianLam: thời gian làm bài (giây)
                danhSachCauTraLoi              // cauTraLoi: danh sách câu trả lời
        );

        // Gửi lên API
        guiLichSuLenAPI(lichSuRequest);

        Intent intent = new Intent(BaiTapDocActivity.this, KetQuaActivity.class);

        // Truyền kết quả CHÍNH XÁC vừa tính được
        intent.putExtra(KetQuaActivity.EXTRA_CORRECT_ANSWERS, soCauDung);
        intent.putExtra(KetQuaActivity.EXTRA_TOTAL_QUESTIONS, danhSachCauHoi.size());

        intent.putExtra(KetQuaActivity.EXTRA_TIME_SPENT, soGiayLamBai);

        intent.putExtra("ID_BAI_TAP", idBaiTapHienTai);
        intent.putExtra(KetQuaActivity.EXTRA_TOPIC, "Đọc");
        intent.putExtra(KetQuaActivity.EXTRA_LEVEL, capDoHienTai);

        startActivity(intent);
        finish();
    }

    // --- METHOD GỬI LỊCH SỬ LÊN API ---
    private void guiLichSuLenAPI(LichSuBaiTapRequest lichSuRequest) {
        ApiService apiService = ApiClient.getClient(this).create(ApiService.class);
        apiService.submitLichSuBaiTap(lichSuRequest).enqueue(new Callback<ApiResponse<Object>>() {
            @Override
            public void onResponse(Call<ApiResponse<Object>> call, Response<ApiResponse<Object>> response) {
                if (response.isSuccessful()) {
                    Log.d("SUBMIT_SUCCESS", "Lịch sử bài tập đã được lưu thành công!");
                } else {
                    Log.e("SUBMIT_ERROR", "Lỗi khi lưu lịch sử: " + response.code());
                }
            }

            @Override
            public void onFailure(Call<ApiResponse<Object>> call, Throwable t) {
                Log.e("SUBMIT_FAILURE", "Lỗi kết nối: " + t.getMessage());
            }
        });
    }

    // --- SỬA ĐỔI 3: CẬP NHẬT MAP KHI CHỌN ĐÁP ÁN ---
    @Override
    public void khiDapAnDuocChon(int maCauHoi, String dapAnDuocChon) {
        if (dapAnDuocChon != null) {
            // Lưu vào Map: ID câu hỏi -> Nội dung chọn (Ví dụ: 1 -> "Father")
            danhSachDapAnNguoiDung.put(maCauHoi, dapAnDuocChon);
        } else {
            danhSachDapAnNguoiDung.remove(maCauHoi);
        }
        capNhatTrangThaiTienTrinh();
    }

    private void capNhatTrangThaiTienTrinh() {
        // Đếm số lượng phần tử trong Map để biết đã làm bao nhiêu câu
        int soCauDaTraLoi = danhSachDapAnNguoiDung.size();
        int tongSoCau = danhSachCauHoi.size();

        int phanTram = (tongSoCau > 0) ? (int) (((float) soCauDaTraLoi / (float) tongSoCau) * 100) : 0;

        tvDemSoCauHoi.setText("Câu " + soCauDaTraLoi + "/" + tongSoCau);
        tvPhanTramTienTrinh.setText(phanTram + "%");
        thanhTienTrinh.setProgress(soCauDaTraLoi);

        if (soCauDaTraLoi == tongSoCau && tongSoCau > 0) {
            btnHoanThanh.setEnabled(true);
            btnHoanThanh.setBackgroundTintList(ColorStateList.valueOf(getResources().getColor(R.color.royalBlue)));
        } else {
            btnHoanThanh.setEnabled(false);
            btnHoanThanh.setBackgroundTintList(ColorStateList.valueOf(getResources().getColor(android.R.color.darker_gray)));
        }
    }
}

package com.example.englishlearningapp.DTO.Request;

import com.google.gson.annotations.SerializedName;

public class CauTraLoiRequest {
    @SerializedName("idCauHoi")
    private int idCauHoi;

    @SerializedName("userAns")
    private String userAns;

    @SerializedName("correctAns")
    private String correctAns;

    public CauTraLoiRequest(int idCauHoi, String userAns, String correctAns) {
        this.idCauHoi = idCauHoi;
        this.userAns = userAns;
        this.correctAns = correctAns;
    }

    public int getIdCauHoi() {
        return idCauHoi;
    }

    public void setIdCauHoi(int idCauHoi) {
        this.idCauHoi = idCauHoi;
    }

    public String getUserAns() {
        return userAns;
    }

    public void setUserAns(String userAns) {
        this.userAns = userAns;
    }

    public String getCorrectAns() {
        return correctAns;
    }

    public void setCorrectAns(String correctAns) {
        this.correctAns = correctAns;
    }
}

package com.example.englishlearningapp.DTO.Response;

import com.google.gson.annotations.SerializedName;
import org.json.JSONException;
import org.json.JSONObject;
import java.io.Serializable;

public class CauHoiResponse implements Serializable {
    @SerializedName("id")
    private int id;

    @SerializedName("noiDungCauHoi")
    private String noiDung;

    @SerializedName("loaiCauHoi")
    private String loaiCauHoi;

    @SerializedName("duLieuDapAn")
    private String jsonDapAn;

    @SerializedName("giaiThich")
    private String giaiThich;

    @SerializedName("audioUrl")
    private String audioUrl;

    // Các biến hỗ trợ hiển thị
    private String dapAnA, dapAnB, dapAnC, dapAnD;

    private String dapAnDung;

    private String dapAnDungRaw;

    // Biến lưu đáp án người dùng chọn (A, B...)
    private String dapAnDaChon = "";

    // --- HÀM XỬ LÝ DỮ LIỆU (ĐÃ SỬA) ---
    public void xuLyDuLieu() {
        try {
            if (jsonDapAn == null || jsonDapAn.isEmpty()) return;

            JSONObject json = new JSONObject(jsonDapAn);

            // 1. Lấy nội dung các đáp án ABCD
            this.dapAnA = json.optString("A", "");
            this.dapAnB = json.optString("B", "");
            this.dapAnC = json.optString("C", "");
            this.dapAnD = json.optString("D", "");

            // 2. Lấy đáp án đúng THÔ (A, B, C, D hoặc từ điền khuyết)
            // Đây là cái dùng để so sánh tính điểm
            String correctKey = json.optString("Correct", "").trim();
            this.dapAnDungRaw = correctKey;

            // 3. Lấy nội dung hiển thị của đáp án đúng (để hiện lời giải)
            if (json.has(correctKey)) {
                // Nếu Correct="A", json có key "A" -> Lấy nội dung của A (VD: "Father")
                this.dapAnDung = json.optString(correctKey);
            } else {
                // Nếu không phải ABCD (dạng điền từ), nội dung chính là key
                this.dapAnDung = correctKey;
            }

        } catch (JSONException e) {
            e.printStackTrace();
        }
    }

    // --- Getter & Setter ---

    public String getDapAnDungRaw() {
        return dapAnDungRaw; // Dùng hàm này để chấm điểm
    }

    public String getDapAnDaChon() { return dapAnDaChon; }
    public void setDapAnDaChon(String dapAnDaChon) { this.dapAnDaChon = dapAnDaChon; }

    public int getId() { return id; }
    public void setId(int id) { this.id = id; }

    public String getNoiDung() { return noiDung; }
    public void setNoiDung(String noiDung) { this.noiDung = noiDung; }

    public String getLoaiCauHoi() { return loaiCauHoi; }
    public void setLoaiCauHoi(String loaiCauHoi) { this.loaiCauHoi = loaiCauHoi; }

    public String getJsonDapAn() { return jsonDapAn; }
    public void setJsonDapAn(String jsonDapAn) { this.jsonDapAn = jsonDapAn; }

    public String getGiaiThich() { return giaiThich; }
    public void setGiaiThich(String giaiThich) { this.giaiThich = giaiThich; }

    public String getAudioUrl() { return audioUrl; }
    public void setAudioUrl(String audioUrl) { this.audioUrl = audioUrl; }

    public String getDapAnA() { return dapAnA; }
    public String getDapAnB() { return dapAnB; }
    public String getDapAnC() { return dapAnC; }
    public String getDapAnDung() { return dapAnDung; }
    public String getDapAnD() { return dapAnD; }
}

package com.example.englishlearningapp.DTO.Response;

import com.google.gson.annotations.SerializedName;
import org.json.JSONException;
import org.json.JSONObject;
import java.io.Serializable;

public class CauHoiResponse implements Serializable {
    @SerializedName("id")
    private int id;

    @SerializedName("noiDungCauHoi")
    private String noiDung;

    @SerializedName("loaiCauHoi")
    private String loaiCauHoi;

    @SerializedName("duLieuDapAn")
    private String jsonDapAn;

    @SerializedName("giaiThich")
    private String giaiThich;

    @SerializedName("audioUrl")
    private String audioUrl;

    // Các biến hỗ trợ hiển thị
    private String dapAnA, dapAnB, dapAnC, dapAnD;

    private String dapAnDung;

    private String dapAnDungRaw;

    // Biến lưu đáp án người dùng chọn (A, B...)
    private String dapAnDaChon = "";

    // --- HÀM XỬ LÝ DỮ LIỆU (ĐÃ SỬA) ---
    public void xuLyDuLieu() {
        try {
            if (jsonDapAn == null || jsonDapAn.isEmpty()) return;

            JSONObject json = new JSONObject(jsonDapAn);

            // 1. Lấy nội dung các đáp án ABCD
            this.dapAnA = json.optString("A", "");
            this.dapAnB = json.optString("B", "");
            this.dapAnC = json.optString("C", "");
            this.dapAnD = json.optString("D", "");

            // 2. Lấy đáp án đúng THÔ (A, B, C, D hoặc từ điền khuyết)
            // Đây là cái dùng để so sánh tính điểm
            String correctKey = json.optString("Correct", "").trim();
            this.dapAnDungRaw = correctKey;

            // 3. Lấy nội dung hiển thị của đáp án đúng (để hiện lời giải)
            if (json.has(correctKey)) {
                // Nếu Correct="A", json có key "A" -> Lấy nội dung của A (VD: "Father")
                this.dapAnDung = json.optString(correctKey);
            } else {
                // Nếu không phải ABCD (dạng điền từ), nội dung chính là key
                this.dapAnDung = correctKey;
            }

        } catch (JSONException e) {
            e.printStackTrace();
        }
    }

    // --- Getter & Setter ---

    public String getDapAnDungRaw() {
        return dapAnDungRaw; // Dùng hàm này để chấm điểm
    }

    public String getDapAnDaChon() { return dapAnDaChon; }
    public void setDapAnDaChon(String dapAnDaChon) { this.dapAnDaChon = dapAnDaChon; }

    public int getId() { return id; }
    public void setId(int id) { this.id = id; }

    public String getNoiDung() { return noiDung; }
    public void setNoiDung(String noiDung) { this.noiDung = noiDung; }

    public String getLoaiCauHoi() { return loaiCauHoi; }
    public void setLoaiCauHoi(String loaiCauHoi) { this.loaiCauHoi = loaiCauHoi; }

    public String getJsonDapAn() { return jsonDapAn; }
    public void setJsonDapAn(String jsonDapAn) { this.jsonDapAn = jsonDapAn; }

    public String getGiaiThich() { return giaiThich; }
    public void setGiaiThich(String giaiThich) { this.giaiThich = giaiThich; }

    public String getAudioUrl() { return audioUrl; }
    public void setAudioUrl(String audioUrl) { this.audioUrl = audioUrl; }

    public String getDapAnA() { return dapAnA; }
    public String getDapAnB() { return dapAnB; }
    public String getDapAnC() { return dapAnC; }
    public String getDapAnDung() { return dapAnDung; }
    public String getDapAnD() { return dapAnD; }

    // Getter cho đáp án thô (A, B, C, D)
    public String getDapAnDungRawAnswer() {
        return dapAnDungRaw; // Trả về "A", "B", "C", "D" hoặc nội dung câu
    }
}

