    Call<List<BaiHocResponse>> getBaiHocByKhoaHoc(@Path("idKhoaHoc")int id);

    @GET("baitap/baihoc/{idbaihoc}")
    @Headers("No-Authentication: true")
    Call<List<BaiTap>> getBaiTapByBaiHocId(@Path("idbaihoc")int id);

    @GET("tuvung/baihoc/{idbaihoc}")
    @Headers("No-Authentication: true")
    Call<List<TuVungResponse>> getTuVungByBaihocID(@Path("idbaihoc") int id);
    @GET("nguphap/bai-hoc/{id}")
    Call<List<NguPhap>> getListNguPhap(@Path("id") int idBaiHoc);
    @GET("tientrinhbaihoc/gan-nhat")
    Call<BaiHocGanNhatResponse> getBaiHocGanNhat();

    @GET("lichsu/user")
    Call<List<LichSuBaiTapResponse>> getLichSuHocTap();

    @GET("bai-lam/lich-su/{id}")
    Call<List<ChiTietBaitapResponse>> getChiTietBaiLam(@Path("id") int idLichSu);
